    const mainContainer = document.querySelector(".main");
    mainContainer.innerHTML +=
        `<div class="card">
          <span class="card--name" id="${episodes.id}">${episodes.name}</span>
          <button class="button">${episodes.episode}</button>

        </div>`;
}
